"use strict";angular.module("tripinApp").service("userService",function($http,$q,env){function getUsers(){return $http({method:"GET",url:env.TRIPINAPIURL+"users",headers:{"Content-Type":"application/json"}}).then(handleSuccess,handleError)}function handleSuccess(response){return response.data}function handleError(response){if(!angular.isObject(response.data)||!response.data.message)return $q.reject("An unknown error occured.")}function updateUser(user){return $http({method:"PUT",url:env.TRIPINAPIURL+"users",data:user,headers:{"Content-Type":"application/json"}}).then(handleSuccess,handleError)}function addNewUser(user){return $http({method:"POST",url:env.TRIPINAPIURL+"users",data:user,headers:{"Content-Type":"application/json"}}).then(handleSuccess,handleError)}function deleteUser(id){return $http({method:"DELETE",url:env.TRIPINAPIURL+"users/"+id,headers:{"Content-Type":"application/json"}}).then(handleSuccess,handleError)}return{getUsers:getUsers,updateUser:updateUser,deleteUser:deleteUser,addNewUser:addNewUser}});